import React from "react";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { deleteTodo, toggleStatusTodo } from "../redux/modules/todos.js";

const Detail = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  const navigate = useNavigate();

  const todo = useSelector((state) => state.todos.todos.find((item) => item.id === id));

  const onDeleteTodo = () => {
    dispatch(deleteTodo(todo.id));
    navigate("/");
  };

  const onToggleStatusTodo = () => {
    dispatch(toggleStatusTodo(todo.id));
  };

  if (!todo) {
    return null; // Todo가 존재하지 않을 경우에 대한 처리
  }

  return (
    <StContainer>
      <StDialog>
        <div>
          <StDialogHeader>
            <div>ID: {todo.id}</div>
            <StButton borderColor="#ddd" onClick={() => navigate("/")}>
              이전으로
            </StButton>
          </StDialogHeader>
          <StTitle>{todo.title}</StTitle>
          <StBody>{todo.body}</StBody>
        </div>
        <StDialogFooter>
          <StButton borderColor="red" onClick={onDeleteTodo}>
            삭제하기
          </StButton>
          <StButton borderColor="green" onClick={onToggleStatusTodo}>
            {todo.isDone ? "취소!" : "완료!"}
          </StButton>
        </StDialogFooter>
      </StDialog>
    </StContainer>
  );
};

export default Detail;

const StContainer = styled.div`
  border: 2px solid #eee;
  width: 100%;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const StDialog = styled.div`
  width: 600px;
  height: 400px;
  border: 1px solid #eee;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const StDialogHeader = styled.div`
  display: flex;
  height: 80px;
  justify-content: space-between;
  padding: 0 24px;
  align-items: center;
`;

const StTitle = styled.h1`
  padding: 0 24px;
`;

const StBody = styled.main`
  padding: 0 24px;
`;

const StDialogFooter = styled.footer`
  display: flex;
  justify-content: flex-end;
  padding: 12px;
  gap: 12px;
`;

const StButton = styled.button`
  border: 1px solid ${({ borderColor }) => borderColor};
  height: 40px;
  width: 120px;
  background-color: #fff;
  border-radius: 12px;
  cursor: pointer;
`;
